var $string = require("../$string.js");
var assert = require('assert');

describe("$string.replaceAll",function(){
	it('',function(){
		assert.equal(
			$string.replaceAll("abab", 'a', 'b'),
			"bbbb"
		);
	});	
});

describe("$string.format", function() {
	it('', function() {
		var original = "{0}+{1}={2}";
		var result = $string.format(original, 1, 1, 2);

		//assert.equal(函数得到的值=实际值,主观意识得到=expect值)
		assert.equal(result,"1+1=2");
	});
});