var $string = require("../$string.js");
var assert = require('assert');

//TODO : 完成test
describe("确定位置$string.", function() {
	var str = "testABCD1234xyz";
	describe('startWith', function() {
		it('', function() {
			assert($string.startWith(str, "test"));
		});

		it("ignore case", function() {
			assert($string.startWith(str, "Test",true));
		});
	});

	describe('endWith', function() {
		it('',function(){
			assert($string.endWith(str, "xyz"));
		});
		it('ignore case',function(){
			assert($string.endWith(str, "XyZ",true));
		});
	});

	describe('contains', function() {
		it('',function(){
			assert($string.contains(str, "ABCD"));
		});
		it('ignore case',function(){
			assert($string.contains(str, "abCD",true));
		});
	});
});