var $string = require("../$string.js");
var assert = require('assert');

describe("$string.trim",function(){
	var str = "{{{{{)))abcd((()))}}}}}";
	it('Left',function(){
		assert.equal(
			$string.trimLeft(str,"{"),
			")))abcd((()))}}}}}"
		);
		assert.equal(
			$string.trimLeft(str,"{)"),
			"abcd((()))}}}}}"
		);
	});

	it('Right',function(){
		assert.equal(
			$string.trimRight(str,'}'),
			"{{{{{)))abcd((()))"
		);
		assert.equal(
			$string.trimRight(str,'})'),
			"{{{{{)))abcd((("
		);		
	});
	
	it('',function(){
		assert.equal(
			$string.trim(str,"{}()"),
			"abcd"
		);
	});
});