var $ = require('../$string.js');
var assert = require('assert');



describe("使用$string('str') wrapper", function() {
	var $test;		
	beforeEach(function(){
		$test = $("abcdefg123456789abcdefg");
	});

	it('trim', function(done) {
		$test.trimLeft('a').trimRight('g').trim("bf"); //trim
		assert.equal($test.str, "cdefg123456789abcde");
		done();
	});

	it('str 与 toString()', function(done) {
		assert( $test.str === $test.toString() );
		done();
	});

	it('startWith-and-ignoreCase',function(done){
		//会改变状态
		// console.log($test.toString());
		assert( $test.startWith("ABC",true) === true );
		done();
	});
	it('endWith-and-ignoreCase',function(done){
		assert( $test.endWith("EFG",true) === true );
		done();
	});
	it('contains-and-ignoreCase',function(done){
		assert( $test.contains("abcdefg",true) === true );
		done();
	});
});